/**
 * ЗАПОЛНЕНИЕ ДАННЫХ ПО ВЫБРАННОМУ ЯЗЫКУ
 *
 * @param {string} lang сокращенное название языка
 */
const currentLang = localStorage.getItem("lang")
  ? localStorage.getItem("lang")
  : "ru";

const langs = {
  ru: {
    title: "Рус",
    icon: "assets/images/langs/ru.svg",
  },
  en: {
    title: "Eng",
    icon: "assets/images/langs/en.svg",
  },
};

const setLangs = (lang) => {
  const elems = document.querySelectorAll("[data-lang-key]");

  const htmlElement = document.getElementsByTagName("html")[0];
  htmlElement.setAttribute("lang", lang);

  elems.forEach((elem) => {
    if (!elem.hasAttribute("data-lang-key")) return;

    const key = elem.dataset.langKey;
    if (!translateData[lang].hasOwnProperty(key)) return;

    elem.innerHTML = translateData[lang][key];
  });
};

const $currentLang = document.querySelector(".js-current-lang");
$currentLang.innerHTML = `<img src="${langs[currentLang].icon}">${langs[currentLang].title}`;
setLangs(currentLang);

const langSwitcher = document.querySelectorAll(".js-lang-switcher");
langSwitcher.forEach((switcher) => {
  switcher.addEventListener("click", (e) => {
    const target = e.target.classList.contains("js-lang-switcher")
      ? e.target
      : e.target.closest(".js-lang-switcher");
    const selectedHTML = target.innerHTML; // img + text
    const selectedLang = target.getAttribute("id");

    $currentLang.innerHTML = selectedHTML;
    localStorage.setItem("lang", selectedLang);

    setLangs(selectedLang);
  });
});
